mapper.update:
update staffs 
set join_date=to_char(sysdate, 'yyyy/mm/dd')
where staff_id=#{staffId}
and branch_seq=#{branchSeq}
and join_date is null

update staffs 
set leave_date=to_char(sysdate, 'yyyy/mm/dd')
where staff_id=#{staffId}
and branch_seq=#{branchSeq}
and join_date=(select MAX(join_date) from staffs
where staff_id=#{staffId}
and branch_seq=#{branchSeq})

mapper.select:
select s.staff_id, u.profile_photo, u.name, u.gender, u.birth, s.join_date, s.leave_date, s.work_part, s.bank_name, s.account_num, s.resume_file, s.health_file, s.bank_file, u.phone_num 
from users u, staffs s 
where staff_id=#{staffId} 
and branch_seq=#{branchSeq} 
and u.user_id=s.staff_id		
and join_date=(
select max(join_date) 
from staffs 
where staff_id=#{staffId} 
and branch_seq=#{branchSeq})

mapper.update:
update staffs 
set resume_file=#{resumeFile}, health_file=#{healthFile}, bank_file=#{bankFile}, bank_name=#{bankName}, account_num=#{accountNum} 
where staff_id=#{staffId}
and branchSeq=#{branchSeq}

update staffs 
set work_part=#{workPart}
where staff_id=#{staffId}
and branch_seq=#{branchSeq} 
and join_date is not null
and leave_date is null

mapper.select:
select staff_id, branch_seq, join_date, leave_date 
from staffs 
where staff_id=#{staffId} 
and branch_seq=#{branchSeq}


	and join_date is null


	and join_date=#{joinDate}




	and leave_date is null


	and leave_date=#{leaveDate}
